import React from 'react'
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Box, Button, CardActionArea, CardActions, Link } from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';

const ProductPreviewCard = () => {
    return (
        <Box sx={{ maxWidth: 345 }} align="center">
            <CardActionArea>
            <CardMedia
                component="img"
                height="100%"
                image="/images/holder_2.jpg"
                sx={{transition: "transform 0.55s ease-in-out", "&:hover": { transform: "scale3d(1.0, 1, 1)" }}}
                onMouseEnter={() => console.log('mouse enter')}
                alt=""
            />
            </CardActionArea>

            <CardContent>
                <Link to="/" component={RouterLink} underline="hover" color="inherit">
                    Lorem, ipsum dolor.
                </Link>
                {/* <Typography variant="body2" sx={{textTransform: 'uppercase'}}>
                    
                </Typography> */}
            </CardContent>

            <Typography sx={{fontWeight: 'bold'}}>
                $18.00 - $75.00
            </Typography>
            
        </Box>
    )
}

export default ProductPreviewCard
