import { Breadcrumbs } from '@mui/material'
import React from 'react'
import Link from '@mui/material/Link';
import {  useParams } from 'react-router-dom';
import { Link as RouterLink } from 'react-router-dom';


const BreadCrumbs = (props) => {
    
    const { param1, param2 } = useParams();
    const fontSizeToUse = props.isFullPath ? "1rem" : "1.8rem";
    const breadCrumbText1 = param1.replaceAll("-", " ");
    const breadCrumbText2 = param2 ? param2.replaceAll("-", " ") : param2;

    return (
        <Breadcrumbs aria-label="breadcrumb" sx={{textTransform: 'uppercase', fontSize: fontSizeToUse}}>
            {props.isFullPath && 
            <Link 
                underline="none"
                color="inherit"
                component={RouterLink}
                to="/"
            >
                home
            </Link>
            }

            <Link
                underline="none"
                color="inherit"
                component={RouterLink}
                to={`/product-category/${param1}`}
                sx={{fontWeight: !param2 ? 'bold': ''}}
            >
                {breadCrumbText1}
            </Link>

            {param2 && <Link
                underline="none"
                color="inherit"
                component={RouterLink}
                to={`/product-category/${param1}/${param2}`}
                sx={{fontWeight: 'bold'}}
            >
                {breadCrumbText2}
            </Link>
            }
        </Breadcrumbs>
    )
}

export default BreadCrumbs
