import React, {useState} from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import CssBaseline from '@mui/material/CssBaseline';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import ListSubheader from '@mui/material/ListSubheader';
import Collapse from '@mui/material/Collapse';
import DraftsIcon from '@mui/icons-material/Drafts';
import SendIcon from '@mui/icons-material/Send';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import StarBorder from '@mui/icons-material/StarBorder';
import { Link, useNavigate } from 'react-router-dom';



const BrowserDrawer = () => {
    const drawerWidth = 240;
    const navigate = useNavigate()

    const [openRoast, setRoastOpen] = useState(false);
    const [openRegion, setRegionOpen] = useState(false);
    const [selectedIndex, setSelectedIndex] = useState('0');
    const [selected, setSelected] = useState('Roast');


    const roastOptions = ["Light", "Medium", "Dark", "Decaf"];
    const regionOptions = ["Central Ameria", "South America", "Compositions"];

    const handleRoastOpen = () => {
        setRoastOpen(!openRoast);
    }

    const handleRegionOpen = () => {
        setRegionOpen(!openRegion);
    }

    const slugify = (toClean) => {
        return toClean.toLowerCase().replace(/\s+/g, " ").replace(/\s/g, "-");
    }

    console.log("logged");

    return (

        <Drawer
          sx={{
            width: drawerWidth,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: drawerWidth,
              boxSizing: 'border-box',
            },
          }}
          variant="permanent"
          anchor="left"
        >
        <List
            sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
            component="nav"
            aria-labelledby="nested-list-subheader"
            subheader={
                <ListSubheader component="div" id="nested-list-subheader">
                    BROWSE
                </ListSubheader>
            }
        >
     
        {/* ROAST */}
        <ListItemButton onClick={handleRoastOpen} >
            <ListItemText primary="Roast" />
            {openRoast ? <ExpandLess /> : <ExpandMore />}
        </ListItemButton>
        <Collapse in={openRoast} timeout="auto">
            <List component="div" disablePadding>
                {roastOptions.map(roast => {
                    return <ListItemButton component={Link} to={`/product-category/roast/${slugify(roast)}`} key={roast}>
                                <ListItemText 
                                    key={roast} 
                                    primary={roast} 
                                    sx={{ pl: 2 }} />
                            </ListItemButton>
                })}
            </List>
        </Collapse>

        {/* REGION */}
        <ListItemButton onClick={handleRegionOpen} >
            <ListItemText primary="Region" />
            {openRegion ? <ExpandLess /> : <ExpandMore />}
        </ListItemButton>
        <Collapse in={openRegion} timeout="auto" unmountOnExit>
            <List component="div" disablePadding>
                {regionOptions.map(region => {
                    return <ListItemButton component={Link} to={`/product-category/region/${slugify(region)}`} key={region}>
                                <ListItemText 
                                    key={region} 
                                    primary={region} 
                                    sx={{ pl: 2 }} />
                            </ListItemButton>
                })}
            </List>
        </Collapse> 

        {/* COLD BREW */}
        <ListItemButton component={Link} to="/product-category/cold-brew" >
            <ListItemText primary="Cold Brew" />
        </ListItemButton>


        </List>
    </Drawer>
    )
}

export default BrowserDrawer;
