import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import Input from '@mui/material/Input';
import FilledInput from '@mui/material/FilledInput';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import TextField from '@mui/material/TextField';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { Button } from '@mui/material';
import { useAuth } from './AuthProvider';
import { useNavigate } from "react-router-dom";

const Login = (props) => {

    const { setAuth } = useAuth();
    const navigate = useNavigate();

    const textFieldStyle = {width: {xs: "95%", sm: "95%", md: "60%"} , marginBottom: "25px"};
    const [userName, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [hasUsernameError, setUsernameError] = useState(false);
    const [hasPasswordError, setPasswordError] = useState(false);
    
    const handleLogin = (e) => {
        e.preventDefault();

        if (!userName || !password) {
            !userName ? setUsernameError(true) : setUsernameError(false);
            !hasPasswordError ? setPasswordError(true) : setPasswordError(false);
            console.log(hasUsernameError, hasPasswordError);
            return;
        }

        setAuth(true);
        navigate("/");
    }


    return (
        <Box align="center">
            <FormGroup >
                <FormControl>
                <TextField error={hasUsernameError ? true : false} onChange={(e) => setUsername(e.target.value.trim())} value={userName} sx={textFieldStyle} label="Username or email" required />
                <TextField error={hasPasswordError ? true : false} onChange={(e) => setPassword(e.target.value.trim())} value={password} sx={textFieldStyle} label="Password" required />
                <FormControlLabel control={<Checkbox defaultChecked />} label="Remember me" />

                <Button onClick={(e) => handleLogin(e)} sx={{width: "100px"}} variant="contained">LOG IN</Button>
                </FormControl>

            </FormGroup>



        </Box>
    )
}

export default Login
