import React, {useState} from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import CssBaseline from '@mui/material/CssBaseline';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import ListSubheader from '@mui/material/ListSubheader';
import Collapse from '@mui/material/Collapse';
import DraftsIcon from '@mui/icons-material/Drafts';
import SendIcon from '@mui/icons-material/Send';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import StarBorder from '@mui/icons-material/StarBorder';
import { Link, useNavigate } from 'react-router-dom';
import { getDropDownForLearning } from '../../util/AdminUtil';
import ButtonGroup from '@mui/material/ButtonGroup';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import ClickAwayListener from '@mui/material/ClickAwayListener';
import Grow from '@mui/material/Grow';
import Paper from '@mui/material/Paper';
import Popper from '@mui/material/Popper';
import MenuItem from '@mui/material/MenuItem';
import MenuList from '@mui/material/MenuList';
import Button from '@mui/material/Button';
import { Stack } from '@mui/system';

const BrowserDrawer = () => {
    const drawerWidth = 240;
    const navigate = useNavigate()

    const options = ['Create a merge commit', 'Squash and merge', 'Rebase and merge'];
    const [openRoast, setRoastOpen] = useState(false);
    const [openRegion, setRegionOpen] = useState(false);
    // const [selectedIndex, setSelectedIndex] = useState('0');
    const [selected, setSelected] = useState('Roast');


    const roastOptions = ["Light", "Medium", "Dark", "Decaf"];
    const regionOptions = ["Central Ameria", "South America", "Compositions"];

    const handleRoastOpen = () => {
        setRoastOpen(!openRoast);
    }

    const handleRegionOpen = () => {
        setRegionOpen(!openRegion);
    }

    const slugify = (toClean) => {
        return toClean.toLowerCase().replace(/\s+/g, " ").replace(/\s/g, "-");
    }

    const learningOptions = getDropDownForLearning();

    const [openLearning, setOpenLearning] = useState(false);

    const handleLearningOpen = () => {
        setOpenLearning(!openLearning);
    };

    const [mobileOpen, setMobileOpen] = useState(false);

    const handleDrawerToggle = () => {
        setMobileOpen((prevState) => !prevState);
    };


    const [open, setOpen] = React.useState(false);
    const anchorRef = React.useRef(null);
    const [selectedIndex, setSelectedIndex] = React.useState(1);
  
    const handleClick = () => {
      console.info(`You clicked ${options[selectedIndex]}`);
    };
  
    const handleMenuItemClick = (event, index) => {
      setSelectedIndex(index);
      setOpen(false);
    };
  
    const handleToggle = () => {
      setOpen((prevOpen) => !prevOpen);
    };
  
    const handleClose = (event) => {
      if (anchorRef.current && anchorRef.current.contains(event.target)) {
        return;
      }
  
      setOpen(false);
    };

    const handleOpen = () => {
        alert('clicked');
    }

    return (
        <Box>
            <Box>
                <Typography>
                    BROWSE
                </Typography>
            </Box>


            {/* <ListItemButton onClick={handleLearningOpen}>
               <ListItemText primary="Learning" />
                {openLearning ? <ExpandLess /> : <ExpandMore />}
            </ListItemButton> */}
            <ListItemButton>
               <ListItemText primary="Learning" />
                {openLearning ? <ExpandLess /> : <ExpandMore  onClick={handleLearningOpen} />}
            </ListItemButton>
            <Collapse  in={openLearning} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                {learningOptions.map(option => {
                return <ListItemButton component={Link} to={option.url} key={option.name} sx={{ pl: 4 }}>
                            <ListItemText onClick={handleDrawerToggle} primary={option.name} />
                        </ListItemButton>

                })}
                </List>
            </Collapse>
{/* 
            <Box>
                <Stack direction="row" spacing={20}>
                    <Button >
                        Roast
                        <Box ml={10} mt={1}>
                            <ExpandMore onClick={handleOpen} />
                        </Box>
                        
                    </Button>
                </Stack>
                <Stack direction="column" sx={{borderLeftStyle: "solid", borderWidth: '1px', marginLeft: '2px', padding: '3px'}}>
                    <Link>
                        Light
                    </Link>
                    <Link>
                        Medium
                    </Link>
                    <Link>
                        Dark
                    </Link>
                    <Link>
                        Decaf
                    </Link>
                </Stack>
            </Box> */}



        </Box>
    )
}

export default BrowserDrawer;
