import React, {useState} from 'react';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import Collapse from '@mui/material/Collapse';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import { Link } from 'react-router-dom';
import Button from '@mui/material/Button';
import { getRoastOptions } from '../../util/ShopUtil';

const BrowserDrawer = () => {
    const drawerWidth = 240;

    const roasts = getRoastOptions();

    const [openRoasts, setOpenRoasts] = useState(false);

    const handleOpenRoasts= () => {
        setOpenRoasts(!openRoasts);
    };

    


    return (
        <Box>
            <Box ml={1} mb={1}>
                <Typography>
                    BROWSE
                </Typography>
                <Divider sx={{width: '30px', borderBottomWidth: 3}} />
            </Box>


            <Button component={Link} onClick={() => setOpenRoasts(true)} to="/product-category/roast" sx={{color: 'black'}}>
                <Typography mr={10}>
                    {roasts.category}
                </Typography>
                {openRoasts ? <ExpandLess  onClick={eval(roasts.function)} /> : <ExpandMore  onClick={handleOpenRoasts} />}
            </Button>
            <Collapse in={openRoasts} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                {roasts.subCategories.map(category => {
                return <ListItemButton sx={{width: '200px'}} component={Link} to={category.url} key={category.name} >
                            <ListItemText primary={category.name} />
                        </ListItemButton>

                })}
                </List>
            </Collapse>



        </Box>
    )
}

export default BrowserDrawer;
