import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import Input from '@mui/material/Input';
import FilledInput from '@mui/material/FilledInput';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import TextField from '@mui/material/TextField';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';

const Login = (props) => {
    const textFieldStyle = {width: {xs: "95%", sm: "95%", md: "60%"} , marginBottom: "25px"};

    const [userName, setUsername] = useState("");
    const [userNameError, setUsernameError] = useState("");
    
    function handleChange() {

        if (!userName) {
            alert("empty");
        }
    }

    return (
        <Box align="center">
            <form>
                <TextField onChange={handleChange} value={userName} sx={textFieldStyle} label="Username or email" required />
                <TextField  sx={textFieldStyle} label="Password" required />
            </form>



        </Box>
    )
}

export default Login
